		var activescreen = WeUiMgr.GetActiveScreen();
						
		// invite_friend_def.pos_x = json[activescreen].node_positions[0 % nodes_per_screen][0];
		//              invite_friend_def.pos_y = json[activescreen].node_positions[0 % nodes_per_screen][1]; 
