	    // place this boundary square
		this.mNode = new GL2.Node();
	    this.mNode.setPosition( offset * x_mult + gRawWidth*0.5, offset * 0.5 * y_mult + gRawHeight*0.5);
	
		// primitive for the darkened area
		var color = new Core.Color([0,0,0]);
