		this.mPolygon.pushVertex(new GL2.Primitive.Vertex([0,offset*0.5],[0,0],color));
		this.mPolygon.pushVertex(new GL2.Primitive.Vertex([0,-offset*0.5],[0,0],color));
		this.mPolygon.pushVertex(new GL2.Primitive.Vertex([-offset,0],[0,0],color));
		this.mPolygon.setAlpha(alpha);
